import React, { useCallback, useEffect, useState, useRef} from 'react'
import { List, Button, Space,  Row, Col, Menu, Input} from 'antd'
import { DollarCircleOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import { useSelector, useDispatch} from 'react-redux';
import { LoadProductAction, searchProductAction} from '../reducers/product';
import { addCartAction } from '../reducers/cart';
import ProductMenu from './ProductMenu';
import Link from 'next/link';
const { Search } = Input;
const ProductList = () => {

  const { me } = useSelector(state => state.user);
  const { Products, loadProductLoading }  = useSelector(state => state.product);
  const inputRef = useRef('');
  const dispatch = useDispatch();
  const [state, setstate] = useState();
  const LoadProduct = useCallback(() =>{
    dispatch(LoadProductAction(''));
  },[]);

  const onSearch = useCallback((e) =>{
    if(e.target.value !== ''){
      setstate(Products.filter((item)=>{
      return item.title.toLocaleUpperCase().includes(e.target.value.toLocaleUpperCase())
     }));
    }
  },[]);

  const addCart = useCallback((item) =>{
    dispatch(addCartAction(item));
  },[]);

  useEffect(() => {
    
    LoadProduct(); 
  }, [state]);

    const IconText = ({ icon, text }) => (
      <Space>
        {React.createElement(icon)}
        {text}
      </Space>
    );
   

    const style = { background: '#fff', padding: '8px 20px',marginBottom: '60px'};
    return (
      <>
        <Row gutter ={[48, 48]} justify="center">
        <Col span = {6}>
        <Menu
              mode="inline"
              defaultOpenKeys={['sub1']}
              style={{ borderRight: 0 }}
              selectable ={false}
            >
                <Menu.Item key="1">
                <Search placeholder="input search text" ref={inputRef} onChange={(e) => onSearch(e)} enterButton />
                </Menu.Item>
                  <ProductMenu />
          {me &&
                <Menu.Item key="2" icon={<ShoppingCartOutlined />}>
                  <Link href ="/cart">My Cart</Link>
                </Menu.Item>
                } 
        </Menu>
        </Col> 
       
        <Col span = {18}>
        <List   style={style}
                itemLayout="vertical"
                size="large"
                pagination={{
                onChange: page => {
                    console.log(page);
                },
                pageSize: 5,
                }}
                loading = {loadProductLoading}
                dataSource={state}
                renderItem={item => (
                <List.Item
                    key={item.id}
                    actions={[
                    <IconText icon={DollarCircleOutlined} text={item.price} key="list-vertical-star-o" />,
                    me&& <Button icon={<ShoppingCartOutlined />} onClick={() =>addCart(item)}>
                    </Button>,
                    ]}
                    extra={
                    <img
                        width={150}
                        height ={200}
                        alt="logo"
                        src={item.image}
                    />
                    }
                >
                    <List.Item.Meta
                    title={<a href={item.href}>{item.title}</a>}
                    description={item.description}
                    />
                    {item.content}
                </List.Item>
            )}
        />
          </Col>
        </Row>
        </>
    )
}
export default ProductList
